import React from 'react'
let style = {
    textAlign : 'center',
    width : '100%',
    height : '100vh'
}
let color = '#'
let possible = '0123456789abcdef'
for (let i = 1; i < 7; i++) {
  let index = Math.floor(Math.random() * possible.length)
  color +=possible[index] 
}
function RandomColor() {
  return (
    <div style={style}>
      <h2 style={{backgroundColor:color}}>{color}</h2>
    </div>
  )
}

export default RandomColor
